package main
import "testing"
import . "main/pkg/testing_helper"

func TestFindTheDifference(_t *testing.T) {
  i1, i2, o := "abcd", "abcde", byte('e')
  T(_t, S(i1, i2), S(findTheDifference(i1, i2)), S(o))

  i1, i2, o = "", "", 't'
  T(_t, S(i1, i2), S(findTheDifference(i1, i2)), S(o))
}

func TestFindTheDifferenceMap(_t *testing.T) {
  i1, i2, o := "abcd", "abcde", byte('e')
  T(_t, S(i1, i2), S(findTheDifferenceMap(i1, i2)), S(o))

  i1, i2, o = "", "", 't'
  T(_t, S(i1, i2), S(findTheDifferenceMap(i1, i2)), S(o))
}
